import React, { useState } from 'react';
import {
  Drawer,
  List,
  ListItem,
  ListItemButton,
  ListItemIcon,
  ListItemText,
  Typography,
  Box,
  Avatar,
  Divider,
  Collapse,
} from '@mui/material';
import {
  Dashboard,
  FolderOpen,
  People,
  ShoppingCart,
  MenuBook,
  Person,
  Campaign,
  Description,
  Group,
  AccountBalance,
  Business,
  Article,
  Public,
  ChevronRight,
  ExpandMore,
} from '@mui/icons-material';

const drawerWidth = 220;

const favoriteItems = [
  { icon: Dashboard, label: 'Overview', active: false },
  { icon: FolderOpen, label: 'Projects', active: false },
  { icon: People, label: 'Customers', active: false },
  { icon: ShoppingCart, label: 'Orders', active: false },
  { icon: MenuBook, label: 'Online Courses', active: false },
];

const defaultItems = [
  { icon: Person, label: 'User Profile', active: false },
  { icon: Dashboard, label: 'Overview', active: false },
  { icon: FolderOpen, label: 'Projects', active: false },
  { icon: Campaign, label: 'Campaigns', active: false },
  { icon: Description, label: 'Documents', active: false },
  { icon: Group, label: 'Followers', active: false },
  { icon: AccountBalance, label: 'Account', active: false },
  { icon: Business, label: 'Corporate', active: false },
  { icon: Article, label: 'Blog', active: false },
  { icon: Public, label: 'Social', active: false },
];

const Sidebar = ({ onMenuClick }) => {
  const [activeItem, setActiveItem] = useState('Default');
  const [expandedSection, setExpandedSection] = useState('default');

  const handleMenuClick = (item) => {
    setActiveItem(item.label);
    if (onMenuClick) {
      onMenuClick(item.label);
    }
  };

  const toggleSection = () => {
    setExpandedSection(expandedSection === 'default' ? '' : 'default');
  };

  const renderMenuItems = (items, section) => (
    items.map((item, index) => (
      <ListItem key={`${section}-${index}`} disablePadding>
        <ListItemButton
          onClick={() => handleMenuClick(item)}
          sx={{
            borderRadius: 2,
            mx: 1,
            mb: 0.5,
            backgroundColor: activeItem === item.label ? 'primary.50' : 'transparent',
            color: activeItem === item.label ? 'primary.main' : 'text.primary',
            '&:hover': {
              backgroundColor: activeItem === item.label ? 'primary.100' : 'action.hover',
            },
          }}
        >
          <ListItemIcon
            sx={{
              color: activeItem === item.label ? 'primary.main' : 'text.secondary',
              minWidth: 36,
            }}
          >
            <item.icon fontSize="small" />
          </ListItemIcon>
          <ListItemText
            primary={item.label}
            primaryTypographyProps={{
              fontSize: '0.8rem',
              fontWeight: activeItem === item.label ? 600 : 400,
            }}
          />
          <ChevronRight fontSize="small" sx={{ color: 'text.secondary', opacity: 0.5 }} />
        </ListItemButton>
      </ListItem>
    ))
  );

  return (
    <Drawer
      variant="permanent"
      sx={{
        width: drawerWidth,
        flexShrink: 0,
        '& .MuiDrawer-paper': {
          width: drawerWidth,
          boxSizing: 'border-box',
          borderRight: '1px solid',
          borderColor: 'divider',
        },
      }}
    >
      <Box sx={{ p: 3 }}>
        {/* Logo */}
        <Box sx={{ display: 'flex', alignItems: 'center', mb: 4 }}>
          <Avatar
            sx={{
              width: 32,
              height: 32,
              bgcolor: 'text.primary',
              mr: 1,
              fontSize: '0.875rem',
              fontWeight: 700,
            }}
          >
            B
          </Avatar>
          <Typography variant="h6" sx={{ fontWeight: 600 }}>
            ByeWind
          </Typography>
        </Box>

        {/* Favorites Section */}
        <Typography
          variant="caption"
          sx={{
            color: 'text.secondary',
            textTransform: 'uppercase',
            letterSpacing: 1,
            fontWeight: 500,
            mb: 1,
            display: 'block',
          }}
        >
          Favorites
        </Typography>
        <List sx={{ mb: 3 }}>
          {renderMenuItems(favoriteItems, 'favorites')}
        </List>

        {/* Default Section */}
        <ListItemButton
          onClick={toggleSection}
          sx={{
            borderRadius: 2,
            mx: 1,
            mb: 1,
            px: 1,
          }}
        >
          <Avatar
            sx={{
              width: 16,
              height: 16,
              bgcolor: 'primary.main',
              mr: 1,
              fontSize: '0.625rem',
              fontWeight: 700,
            }}
          >
            D
          </Avatar>
          <Typography variant="body2" sx={{ fontWeight: 500, flex: 1 }}>
            Default
          </Typography>
          {expandedSection === 'default' ? <ExpandMore fontSize="small" /> : <ChevronRight fontSize="small" />}
        </ListItemButton>
        <Collapse in={expandedSection === 'default'}>
          <List>
            {renderMenuItems(defaultItems, 'default')}
          </List>
        </Collapse>
      </Box>
    </Drawer>
  );
};

export default Sidebar;
