import React from 'react';
import { Card, CardContent, Typography, Box, Grid } from '@mui/material';
import { TrendingUp, TrendingDown } from '@mui/icons-material';
import { BarChart } from '@mui/x-charts/BarChart';

const ProjectionsChart = () => {
  const data = [20, 35, 25, 45, 30, 40, 35, 50, 45, 35, 40, 30];
  const xLabels = ['Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];

  return (
    <Card sx={{ height: 300 }}>
      <CardContent sx={{ p: 2.5, height: '100%' }}>
        <Typography variant="body1" sx={{ mb: 2, fontWeight: 600, fontSize: '1rem' }}>
          Projections vs Actuals
        </Typography>
        <Box sx={{ height: 220, width: '100%' }}>
          <BarChart
            series={[{ data, color: '#3b82f6' }]}
            xAxis={[{ data: xLabels, scaleType: 'band', tickLabelStyle: { fontSize: 12 } }]}
            height={220}
            margin={{ left: 20, right: 20, top: 20, bottom: 30 }}
            slotProps={{
              bar: {
                rx: 4,
              },
            }}
          />
        </Box>
      </CardContent>
    </Card>
  );
};

const statsData = [
  {
    title: 'Customers',
    value: '3,781',
    change: '+11.01%',
    trend: 'up',
    highlighted: true,
  },
  {
    title: 'Orders',
    value: '1,219',
    change: '-0.03%',
    trend: 'down',
    highlighted: false,
  },
  {
    title: 'Revenue',
    value: '$695',
    change: '+15.03%',
    trend: 'up',
    highlighted: false,
  },
  {
    title: 'Growth',
    value: '30.1%',
    change: '+6.08%',
    trend: 'up',
    highlighted: true,
  },
];

const StatsCards = () => {
  return (
    <Grid container spacing={2} sx={{ mb: 3 }}>
      <Grid item xs={12} md={6}>
        <Grid container spacing={2}>
          {statsData.map((stat, index) => (
            <Grid item xs={6} key={index}>
          <Card
            sx={{
              backgroundColor: stat.highlighted ? '#e0f2fe' : 'background.paper',
              border: 'none',
            }}
          >
            <CardContent sx={{ p: 3 }}>
              <Typography
                variant="body2"
                color="text.secondary"
                sx={{ mb: 1, fontWeight: 500 }}
              >
                {stat.title}
              </Typography>
              <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', mt: 1 }}>
                <Typography variant="h5" sx={{ fontWeight: 700, color: 'text.primary', fontSize: '1.5rem' }}>
                  {stat.value}
                </Typography>
                <Box
                  sx={{
                    display: 'flex',
                    alignItems: 'center',
                    gap: 0.5,
                    color: stat.trend === 'up' ? 'success.main' : 'error.main',
                  }}
                >
                  {stat.trend === 'up' ? (
                    <TrendingUp fontSize="small" />
                  ) : (
                    <TrendingDown fontSize="small" />
                  )}
                  <Typography variant="body2" fontWeight={500}>
                    {stat.change}
                  </Typography>
                </Box>
              </Box>
            </CardContent>
          </Card>
            </Grid>
          ))}
        </Grid>
      </Grid>
      <Grid item xs={12} md={6}>
        <ProjectionsChart />
      </Grid>
    </Grid>
  );
};

export default StatsCards;
